require("./extended-console.js");

process.stdout.write("Log with default timestamp format:\n");
console.log("The quick brown fox jumps over the lazy dog");
process.stdout.write("\n");

process.stdout.write("Info with default timestamp format:\n");
console.info("The quick brown fox jumps over the lazy dog");
process.stdout.write("\n");

process.stdout.write("Warn with default timestamp format:\n");
console.warn("The quick brown fox jumps over the lazy dog");
process.stdout.write("\n");

process.stdout.write("Error with default timestamp format:\n");
console.error("The quick brown fox jumps over the lazy dog");
process.stdout.write("\n");

process.stdout.write("Log with multiple arguments:\n");
console.log("Foo", 123, true, [1, 2, 3]);
process.stdout.write("\n");

process.stdout.write("Log with custom timestamp format:\n");
console.extended.timestampFormat = "YYYY-MM-DD HH:mm";
console.log("The quick brown fox jumps over the lazy dog");
process.stdout.write("\n");

process.stdout.write("Log without timestamp:\n");
console.extended.showTimestamp = false;
console.log("The quick brown fox jumps over the lazy dog");
console.extended.showTimestamp = true;
process.stdout.write("\n");

process.stdout.write("Log without log level:\n");
console.extended.showLogLevel = false;
console.log("The quick brown fox jumps over the lazy dog");
console.extended.showLogLevel = true;
process.stdout.write("\n");

process.stdout.write("Log without log level or timestamp:\n");
console.extended.showLogLevel = false;
console.extended.showTimestamp = false;
console.log("The quick brown fox jumps over the lazy dog");
console.extended.showLogLevel = true;
console.extended.showTimestamp = true;
process.stdout.write("\n");

process.stdout.write("Add event listener on error:\n");
console.extended.on("log", function(a) {
	process.stdout.write("Error event listener called\n");
});
console.log("Error event listener trigger test");
process.stdout.write("\n");
